import React from "react";
import "./Certificates.css";

const certificates = [
  {
    title: "Power BI Certificate",
    tag: "Microsoft Full Stack PowerBI Developer",
    desc: "Turn complex project data into clear, actionable insights through interactive dashboards and real-time visualizations.",
    issuer: "Developer",
    year: "2025",
    url: "https://learn.microsoft.com/en-us/certifications/power-bi-data-analyst-associate/"
  },
  {
    title: "Excel Visual Basic for Applications",
    tag: "Report Automation Specialist using VBA Programming",
    desc: "It’s a programming language used to automate tasks and create custom functions in Microsoft Excel",
    issuer: "Developer",
    year: "2022",
    url: "https://learn.microsoft.com/en-us/certifications/excel-2016-expert/"
  },
  {
    title: "SQL Structured Query Languange",
    tag: "Reporting Specialist",
    desc: "Certified SQL Reporting Specialist with expertise in data querying and analysis.",
    issuer: "Business Reporting Specialist",
    year: "2025",
    url: "https://education.oracle.com/oracle-database-sql/"
  },
  {
    title: "Primavera P6",
    tag: "Project Planning & Scheduling For Construction Management",
    desc: "Skilled in project planning and scheduling using Primavera P6 for effective timeline and resource management.",
    issuer: "Oracle",
    year: "2021",
    url: "https://www.datacamp.com"
  },  
  {
    title: "Power Apps",
    tag: "Report Generation and Workflows Expert",
    desc: "Skilled in creating workflows in Power Apps to streamline project monitoring at both micro and macro levels.",
    issuer: "Oracle",
    year: "2021",
    url: "https://www.datacamp.com"
  },
];

const tools = [
  { name: "Navis Work",    level: "Intermediate", color: "#00B386" },
  { name: "Power Query",   level: "Advanced", color: "#805AD5" },
  { name: "SQL",   level: "Intermediate", color: "#A259FF" },
  { name: "Power Apps",   level: "Intermediate", color: "#FBBF24" },
  { name: "Power Automate",   level: "Advanced", color: "#22C55E" },
  { name: "Primavera P6",    level: "Beginner", color: "#3B82F6" },
];



const Certificates = () => {
  return (
    <div>
    <main className="main"> 
    <div className="custom-section-container">
      <h2 className="certificate-section-title">Certificates & Tools</h2>

      <h3 className="sub-title">Certificates</h3>
      <div className="certificates-grid">
        {certificates.map((cert, index) => (
          <a
            key={index}
            className="certificate-card"
            href={cert.url}
            target="_blank"
            rel="noopener noreferrer"
          >
            <div className="cert-tag">{cert.tag}</div>
            <div className="cert-title">{cert.title}</div>
            <div className="cert-details">
              <p>{cert.desc}</p>
              <span>
                {cert.issuer} • {cert.year}
              </span>
            </div>
          </a>
        ))}
      </div>

      <h3 className="sub-title">Tools & Technologies</h3>
      <div className="tools-grid">
        {tools.map((tool, index) => (
          <div className="tool-card" key={index}>
            <span className="tool-name">{tool.name}</span>
            <span className="tool-level" style={{ backgroundColor: tool.color }}>
              {tool.level}
            </span>
          </div>
        ))}
      </div>
    </div>

    </main>
    </div>
  );
};

export default Certificates;
