import React from "react";
import "./Certificates.css";

const certificates = [
  {
    title: "Power BI Certificate",
    tag: "Data Visualization",
    desc: "Microsoft Full Stack PowerBI Developer",
    issuer: "Microsoft",
    year: "2025",
    url: "https://learn.microsoft.com/en-us/certifications/power-bi-data-analyst-associate/"
  },
  {
    title: "Excel Visual Basic Developer (Excel VBA)",
    tag: "Spreadsheet",
    desc: "Excel Expert Certification",
    issuer: "Microsoft",
    year: "2022",
    url: "https://learn.microsoft.com/en-us/certifications/excel-2016-expert/"
  },
  {
    title: "SQL Certificate",
    tag: "Database",
    desc: "SQL Reporting Specialist",
    issuer: "Oracle",
    year: "2021",
    url: "https://education.oracle.com/oracle-database-sql/"
  },
  {
    title: "Primavera P6",
    tag: "Project Planning and Scheduling",
    desc: "Data Analysis Fundamentals",
    issuer: "Oracle",
    year: "2021",
    url: "https://www.datacamp.com"
  },
];

const tools = [
  {
    name: "Microsoft Excel VBA",
    level: "Expert",
    color: "#00B386",
    logo: "https://cdn.iconscout.com/icon/free/png-256/microsoft-excel-1411847-1194337.png"
  },
  {
    name: "Power BI",
    level: "Advanced",
    color: "#805AD5",
    logo: "https://upload.wikimedia.org/wikipedia/commons/c/cf/New_Power_BI_Logo.svg"
  },
  {
    name: "SQL",
    level: "Intermediate",
    color: "#A259FF",
    logo: "https://cdn-icons-png.flaticon.com/512/4248/4248443.png"
  },
  {
    name: "Power Apps",
    level: "Intermediate",
    color: "#FBBF24",
    logo: "https://upload.wikimedia.org/wikipedia/commons/f/fd/Microsoft_Power_Apps_logo.svg"
  },
  {
    name: "Power Automate",
    level: "Advanced",
    color: "#22C55E",
    logo: "https://upload.wikimedia.org/wikipedia/commons/3/3b/Microsoft_Power_Automate_2020_logo.svg"
  },
  {
    name: "Primavera P6",
    level: "Beginner",
    color: "#3B82F6",
    logo: "https://static.wikia.nocookie.net/logopedia/images/3/34/PrimaveraP6.png"
  }
];


const Certificates = () => {
  return (
    <div>
    <main className="main"> 
    <div className="custom-section-container">
      <h2 className="certificate-section-title">Certificates & Tools</h2>

      <h3 className="sub-title">Certificates</h3>
      <div className="certificates-grid">
        {certificates.map((cert, index) => (
          <a
            key={index}
            className="certificate-card"
            href={cert.url}
            target="_blank"
            rel="noopener noreferrer"
          >
            <div className="cert-tag">{cert.tag}</div>
            <div className="cert-title">{cert.title}</div>
            <div className="cert-details">
              <p>{cert.desc}</p>
              <span>
                {cert.issuer} â€¢ {cert.year}
              </span>
            </div>
          </a>
        ))}
      </div>

      <h3 className="sub-title">Tools & Technologies</h3>
      <div className="tools-grid">
        {tools.map((tool, index) => (
          <div className="tool-card" key={index}>
            <span className="tool-name">{tool.name}</span>
            <span className="tool-level" style={{ backgroundColor: tool.color }}>
              {tool.level}
            </span>
          </div>
        ))}
      </div>
    </div>

    </main>
    </div>
  );
};

export default Certificates;
